typedef long long int ll;

ll A,B,k;

ll func(ll x)
{
	if(x==1) return 0;

	if(k==1)
	{
		return A * (x-1);
	}

	if(x%k==0)
	{
		ll div = x/k;
		ll costOfSubtracting = (x - div)*A;
		if(costOfSubtracting<B) return costOfSubtracting + func(div);
		else return B + func(div);
	}
	else
	{
		ll remaining;
		if(x>k) remaining = x%k;
		else remaining = x-1;
		ll afterval = x - remaining;
		return A*remaining + func(afterval);
	}
}

