int loveTriangle(int *arr,int n)
{
	if(n==2)
		return 0;
	int i;
		for(i=0;i<n;i++)
			if(arr[i]<1 || arr[i]>n)
				return 0;
	
		for(i=0;i<n;i++)
		{
			if(i+1==arr[i])
				continue;
			if(i+1 == arr[arr[i]-1])
				continue;
			if(i+1 == arr[arr[arr[i]-1]-1])
				return 1;
		}
	return 0;
	
}
