#include <stdio.h>
int Ar[5001], Vis[5001];
int Check(int I, int C, int F) {
    if (!C) return I == F;
    int Res = 0;
    Vis[I] = 1;
    Res = Check(Ar[I], C - 1, F);
    Vis[I] = 0;
    return Res;
}
int main() {
    int i, N, Res = 0;
    scanf("%d", &N);
    for (i = 0; i < N; ++i) {
        scanf("%d", Ar + i);
        --Ar[i];
    }
    for (i = 0; i < N; ++i) {
        Res += Check(Ar[i], 2, i);
    }
    puts(Res ? "YES" : "NO");
    return 0;
}

