#include<stdio.h>
int main()
{
	int n;
	int i,j;
	int a[100000];
	while(scanf("%d",&n)!=EOF){
		for(i=0;i<n;i++){
			scanf("%d",&a[i]);
		}
		if(a[n-1]<=500000){
			printf("%d\n",a[n-1]-1);
		}else if(a[0]>500000){
			printf("%d\n",1000000-a[0]);
		}else{
			for(i=0;i<n;i++){
				if(a[i]<=500000&&a[i+1]>500000){
					break;
				}
			}
			if((a[i]-1)>(1000000-a[i+1]))	{
				printf("%d\n",a[i]-1);
			}else{
				printf("%d\n",1000000-a[i+1]);
			}
		}
	}
}

