#include<stdio.h>

int main()
{
    int n, i, v, max = 0;
    scanf("%d", &n);
    for (i = 0; i < n; i++) {
        scanf("%d", &v);
        if (v > 500000) {
            if ((1000000 - v) > max )
                max = 1000000 - v;
        }
        else {
            if ((v - 1) > max)
                max = v - 1;
        }
    }
    printf("%d\n", max);
    return 0;
}

