int arr[1000000];
void merge(int start,int end,int mid)
{
	int aux[1000000],i=start,j=mid+1,p=0;
	while(i<=mid && j<=end)
	{
		if(arr[i]<arr[j]) 
			aux[p++]=arr[i++];
		else
			aux[p++]=arr[j++];
	}
	while(i<=mid)
		aux[p++]=arr[i++];
	while(j<=end)
		aux[p++]=arr[j++];
	for(i=0;i<p;i++)
		arr[start+i]=aux[i];
	return;
}
void mergesort(int start,int end)
{
	int mid=(start+end)/2;
	if(start<end)
	{
		mergesort(start,mid);
		mergesort(mid+1,end);
		merge(start,end,mid);
	}
	return;
}
