#include<stdio.h>
#include<stdlib.h>

int p=1,q=1000000;
int binsearch(int l,int r,int *arr)
{
    int mid=(l+r)/2;

    if((arr[mid+1]-p)<(q-arr[mid+1]) && (arr[mid-1]-p)<(q-arr[mid-1]))
        binsearch(mid+1,r,arr);
    else if((arr[mid+1]-p)>(q-arr[mid+1]) && (arr[mid-1]-p)>(q-arr[mid-1]))
        binsearch(l,mid-1,arr);
    else {
            if((arr[mid]-p)<=(q-arr[mid]))
            return mid;
            else return mid-1;
    }
}

