int find2vo(char *ptr,int *x)
//the main function of that function is to find if there is any two consecutive vowels of the string.
//and it return its position at the string at the variables x,y.
{
    int i=0;
    char flag=0;
    while(*(ptr+i)!='\0')
    {
        if(ifvowl(*(ptr+i))==0)
        {
            i++;
            flag=0;
        }
        else if(ifvowl(*(ptr+i))==1)
        {
            flag++;
            if(flag==2)
            {
                *x=i-1;
                return 1;
            }
            i++;
        }
    }
        return 0;
}
void switching(char *ptr,int x)
{
    while(*(ptr+x))
    {
        *(ptr+x)=*(ptr+x+1);
        x++;
    }
}
