    else
        return 0;
}
void check_vowels(char s[], int n)
{
    int i=0,flg=0;
    while(i<n)
    {
        flg=0;
        if(vow(s[i])&&vow(s[i+1]))
        {
            flg=1;
            int j=i+1;
            while(s[j+1]!='\0'){s[j]=s[j+1];j++;}
            if(s[j]=='\0'){s[i]=s[j];}
            n--;
            s[n]='\0';
        }
        else
            i++;
            
        if(flg)
            i=0;
    }
}

