	scanf("%d", &n);
	while(n) {
		char c = getchar();
		if(c != '\n') {
			if(c == 'a' || c == 'e' || c == 'i' || c == 'o' || c == 'u' || c == 'y') {
				if(b) s[k] = c, k++;
				b = 0;
			}
			else {
				s[k] = c, k++;
				b = 1;
			}
			n--;
		}
	}
	s[k] = 0;
	puts(s);
    return 0;
}

