int main()
{
    char b[101];
    int n,k,j;
    scanf("%d",&n);
    scanf("%s",b);
    for(k=0;b[k]!='\0';k++)
    {
        if(f(b[k])==2&&f(b[k+1])==2)
        {
            for(j=k+1;b[j]!='\0';j++)
            {
                b[j]=b[j+1];
            }
            b[j]='\0';
            k--;
