#include<stdio.h>
void merge(int *b,int low,int high) {
    int mid=(low+high)/2;
    int p1=low,p2=mid+1,k=0,c[high-low+1];
    while(p1<=mid && p2<=high)
        c[k++]=(b[p1]>b[p2])?b[p2++]:b[p1++];
    while(p1<=mid)
        c[k++]=b[p1++];
    while(p2<=high)
        c[k++]=b[p2++];
    for(int i=0;i<k;i++)
        b[i+low]=c[i];
}
void mergesort(int *b,int low,int high) {
    if(low==high)
        return;
    int mid=(low+high)/2;
    mergesort(b,low,mid);
    mergesort(b,mid+1,high);
    merge(b,low,high);
}

