#include<stdio.h>
void quicksort(int a[],int f,int l)
{
	int p,i,j;
	if(f<l)
	{
		p=f;
		i=f;
		j=l;
		while(i<j)
		{
			while(a[i]<=a[p]&&i<l)
			i++;
			while(a[j]>a[p])
			j--;
			if(i<j)
			a[i]=a[i]+a[j]-(a[j]=a[i]);

		}

		a[j]=a[p]+a[j]-(a[p]=a[j]);
		quicksort(a,f,j-1);
		quicksort(a,j+1,l);
	}
}
