#include<stdio.h>
int zero(int a[],int n)
{
	int c=0;
	for(int i=0;i<n;i++)
	{
		if (a[i]==0) c++;
	}
	if (c==n) return 1; else return 0;
}
int findMax(int a[],int n)
{
	int m=a[0];
	for(int i=0;i<n;i++)
	{if(m<a[i]) m=a[i];}
	return m;
}
void replace(int a[],int n,int m)
{
	for(int i=0;i<n;i++)
	{ if (a[i]==m) a[i]=0;}
}
int No_of_ways(int a[],int n)
{
	static int c=0;
	if (zero(a,n))
		return 0;
	int max=findMax(a,n);
	replace(a,n,max);
	c=1+No_of_ways(a,n);
	return c;
}
