#include<stdio.h>
void merge(int *a,int low,int high) {
    int mid=(low+high)/2;
    int p1=low,p2=mid+1,k=0,c[high-low+1];
    while(p1<=mid && p2<=high)
        c[k++]=(a[p1]>a[p2])?a[p2++]:a[p1++];
    while(p1<=mid)
        c[k++]=a[p1++];
    while(p2<=high)
        c[k++]=a[p2++];
    for(int i=0;i<k;i++)
        a[i+low]=c[i];
}
void mergesort(int *a,int low,int high) {
    if(low==high)
        return;
    int mid=(low+high)/2;
    mergesort(a,low,mid);
    mergesort(a,mid+1,high);
    merge(a,low,high);
}

