int printDistinct(int arr[], int n)
{
    int count=0;
    // Pick all elements one by one
    for (int i=0; i<n; i++)
    {
        // Check if the picked element is already printed
        int j;
        for (j=0; j<i; j++)
           if (arr[i] == arr[j])
               break;

        // If not printed earlier, then print it
        if (i == j && arr[i]!=0)
          count++;
    }
    return count;
}

