        for (int j = 0; j < numprimes; j++) {
            if (primes[j] >= cap) break;
            if (i % primes[j] == 0) {
                isprime = false;
                break;
            }
        }
        if (isprime) primes[numprimes++] = i;
    }

    int N,M;

    scanf("%d %d",&M,&N);
    int start = N%2?N:N-1;
    for (int i = start; i > M; i-=2) {
        // printf("Checking %d\n",i);
