int isPrime(int n)
{
	// Corner cases
	if (n <= 1)  return 0;
	if (n <= 3)  return 1;

	// This is checked so that we can skip 
	// middle five numbers in below loop
	if (n % 2 == 0 || n % 3 == 0) return 0;

	for (int i = 5; i*i <= n; i = i + 6)
		if (n%i == 0 || n % (i + 2) == 0)
			return 0;

	return 1;
}

int main()
{
	int y, p;
	int c = 0;
	scanf("%d %d", &p, &y);
	int i = y;
	while (i > p)
	{
		if (i % 2 == 0)
			i--;
		else
		{
			if (isPrime(i))
			{
				printf("%d", i);
				c = 1;
				break;
			}
			if (sqrt(i) <= p)
				i--;
			else
			{
				int j = p;
				int c1 = 0;
				while (j > 2)
				{
					if (i%j == 0)
					{
						c1 = 1;
						break;
					}
					j--;
				}
				if (c1 == 1)
					i--;
				else
				{
					printf("%d", i);
					c = 1;
					break;
				}
			}
		}
	}
	if (c == 0)
		printf("-1");
	return 0;
}

