#define SIEVE_MAX_PRIME 999983
#define SIEVE_PRIME_COUNT 78498

static bool _sieve_is_composite[SIEVE_MAX_PRIME/2+1] = {}; // 2*i+1
static int64_t _sieve_prime[SIEVE_PRIME_COUNT];

static void generate_primes() {
    // sieve
    _sieve_is_composite[0] = true;
    for(int64_t i = 1, j = 3 ; j*j <= SIEVE_MAX_PRIME ; ++i, j+= 2) {
        if(_sieve_is_composite[i]) continue;
        for(int64_t jj = j*j, ii = (jj-1)/2 ; jj <= SIEVE_MAX_PRIME ; jj += 2*j, ii += j)
            _sieve_is_composite[ii] = true;
    }

    // collect primes
    _sieve_prime[0] = 2;
    for(int64_t i = 0, in = (SIEVE_MAX_PRIME-1)/2, pi = 1 ; i <= in ; ++i) {
        if(_sieve_is_composite[i]) continue;
        assert(pi < SIEVE_PRIME_COUNT); _sieve_prime[pi++] = 2*i+1;
    }
}

static inline bool is_prime(int64_t n) {
    if(n <= SIEVE_MAX_PRIME) {
        if(n == 2) return true;
        else if(n % 2 == 0) return false;
        else return !_sieve_is_composite[(n-1)/2];
    } else {
        for(int pi = 0 ; ; ++pi) { const int64_t p = _sieve_prime[pi];
            if(p*p > n) return true;
            if(n % p == 0) return false;
        }
    }
}

static inline bool is_divisible_by_primes_le(int64_t n, int64_t pm) {
    for(int pi = 0 ; ; ++pi) { const int64_t p = _sieve_prime[pi];
        if(p > pm) return false;
        if(p*p > n) return false;
        if(n % p == 0) return true;
    }
    return true;
}

int main() {
    generate_primes();
    int64_t p,y; scanf("%" PRId64 " %" PRId64 "\n", &p,&y);

    // we need to find largest x such that it is not divisible by primes <= pmax
    // if pmax is larger than sqrt(y) than we need biggest prime less than y
    // otherwise search for not divisible by primes smaller than pmax
    if(p*p > y) {
        for(int i = y ; i > p ; --i) if(is_prime(i)) {
            printf("%d\n", i); return 0;
        }
    } else {
        for(int i = y ; i > p ; --i) if(!is_divisible_by_primes_le(i,p)) {
            printf("%d\n", i); return 0;
        }
    }

    printf("-1\n"); return 0;
}

