static int least_prime_divisor( int x )
{
    int i;
    for( i = 2 ; i * i <= x; ++i)
    {
        if ( x % i == 0 )
            return i ;
    }
    
    return x;
}
 
static int solve()
{
    int p, y;
    scanf("%d%d", &p, &y ) ;
    
    while(y > p)
    {
        if (least_prime_divisor(y) > p)
        {
            printf("%d\n", y);
            return 0;
        }
        
        --y;
    }
    
    printf("-1");
     
    return 0;
}

