	double ans2;
	scanf("%lf%lf%lf%lf%lf",&R,&x1,&y1,&x2,&y2);
	if(dist(x1,y1,x2,y2)>=R)
	{
		printf("%lf %lf %lf\n",x1,y1,R);
		return 0;
	}
	else if(x1==x2 && y1==y2)
	{
		r=R/2;
		ans1=x1;
		ans2=(y1+y1+R)/2;
		printf("%lf %lf %lf\n",ans1,ans2,r);
		return 0;
	}
	else if(x1==x2 && y1!=y2)
