		temp1=((-1*B)+sqrt((B*B)-(4*A*C)))/(2*A);
		temp2=((-1*B)-sqrt((B*B)-(4*A*C)))/(2*A);
		temp3=m*(temp1)+c;
		temp4=m*(temp2)+c;
		if(dist(temp1,temp3,x2,y2)>dist(temp2,temp4,x2,y2))
