	if(x==cx && y==cy)printf("%.7f %.7f %.7f\n",cx+(R/(double)2),cy,R/(double)2);
	else{
		d=(cx-x)*(cx-x);
		d+=(cy-y)*(cy-y);
		if(d>=(R*R))printf("%.7f %.7f %.7f\n",cx,cy,R);
		else{
			d=sqrt(d);
			r=(d+R)/(double)2;
			if(cx==x){
				px=cx;
				if(y>cy)py=(y+cy-R)/(double)2;
				else py=(y+cy+R)/(double)2;
			}
