				m=(cy-y)/(cx-x);
				px=sqrt((r*r)/((double)1 + (m*m)));
				if(x>cx)px=-px;
				px+=x;
				py=(m*px) + y - (m*x);
			}
			printf("%.7f %.7f %.7f\n",px,py,r);
