int calculateMoney(char *str, int n){
  int money = 0;
  int x = 0;
  char previous;
  int y = 0;
  for(int i = 0; i < n; i++){
    previous = *(str+i);
    if('U' == *(str+i)){
      y++;
    }
    else if('R' == *(str+i)){
      x++;
    }
    if(x == y){
      i++;
      char new = *(str+i);
      if(previous == new){
        money++;
      }
      else{
        previous = new;
      }
      i--;
    }
  }
  return money;
}

