        for(int j = 0 ; j < M ; ++j) {
            const int64_t prod = _a[i]*_b[j];
            if(prod > _max_prod[i]) _max_prod[i] = prod;
        }
    }
    qsort(_max_prod, N, sizeof(int64_t), compare_i64);
    printf("%" PRId64 "\n", _max_prod[N-2]);

    return 0;
}

