#include<stdio.h>
void combine(long long int arr[],long long int l1,long long int r1,long long int l2,long long int r2)
{
	long long int i1=l1,i2=l2,k=0;
	long long int temp[1000];
	while(i1<=r1 && i2<=r2)
	{
		if(arr[i1]<arr[i2])
		{
			temp[k++]=arr[i1++];
		}
		else
		{
			temp[k++]=arr[i2++];
		}
	}
	while(i1<=r1)
	{
		temp[k++]=arr[i1++];
	}
	while(i2<=r2)
	{
		temp[k++]=arr[i2++];
	}
	for(i1=l1,i2=0;i1<=r2;i1++)
	{
		arr[i1]=temp[i2++];
	}
}
void merge(long long int arr[],long long int l,long long int r)
{
	long long int mid;
	if(l<r)
	{
		mid=(l+r)/2;
		merge(arr,l,mid);
		merge(arr,mid+1,r);
		combine(arr,l,mid,mid+1,r);
	}
}
int main()
{
	long long int n,m,i,j,n1,n2;
