typedef long long int ll;

int max(int a,int b)
{
	if(a<b)
		return b;
	return a;	 
} 
int pro(int a)
{
	int ans=a,t,q;
	while(ans/10!=0)	
	{
		q=1;
		while(ans>0)
		{
			t=ans%10;
			if(t!=0)
				q*=t;
			ans=ans/10;
		}
		ans=q;
	}
	return ans;
} 
int count[1000001][10];
int main()
{
	int l,q,i,j;
	for(i=1;i<=1000000;i++)
	{
		l=pro(i);
		count[i][l]=count[i-1][l]+1;
		for(j=0;j<=9;j++)
		{
			if(j!=l)
			{
				count[i][j]=count[i-1][j];
			}
		}			
	}
	scanf("%d",&q);

	while(q--)
	{
		int l,r,k;
		scanf("%d%d%d",&l,&r,&k);
		printf("%d\n",count[r][k]-count[l-1][k]);
	}

	return 0;
}

#include<stdio.h>
#include<string.h>
#define maxn 1000005
int dp[maxn];
int ans[maxn][10];

int DP(int a)
{
	int j=1;
	if(dp[a]>=0)  return dp[a];
	while(a>=10)
	{
		if(a%10!=0)  j*=a%10;
		a=a/10;
	}
	j=j*a;
	return dp[j];
}

int main()
{
	int Q,l,r,k;
	for(int i=0;i<maxn;i++)
		for(int j=0;j<10;j++)
		   ans[i][j]=0;
	for(int i=0;i<maxn;i++)
	  dp[i]=-1;
	for(int i=1;i<10;i++)
	  dp[i]=i;
	for(int i=10;i<maxn;i++)
	  dp[i]=dp[DP(i)];
	for(int i=1;i<maxn;i++)
	{
		for(int j=0;j<10;j++)
		  ans[i][j]=ans[i-1][j];
		ans[i][dp[i]]++;
	}
	
	while(scanf("%d", &Q)!=EOF)
	{
		while(Q--)
		{
			scanf("%d%d%d", &l, &r, &k);
			printf("%d\n",ans[r][k]-ans[l][k]+(dp[l]==k));
		}
	}
	return 0;
}

