#define N 1000000
int M[N+1][10];

int main() {
	for (int i=0; i<=N; i++) {
		int s = i;
		while (s>=10) {
			int t = 1;
			for (int k=s; k; k/=10)
				if (k%10) t*=k%10;
			s = t;
		}
		M[i][s] = 1;
	}
	for (int d=0; d<10; d++) {
		for (int i=1; i<=N; i++) M[i][d] += M[i-1][d];
	}
	int q;
	while (scanf("%d", &q)==1) {
		for (int i=0; i<q; i++) {
			int a, b, c;
			scanf("%d %d %d", &a, &b, &c);
			printf("%d\n", M[b][c]-M[a-1][c]);
		}
	}
}

