int solve(int value)
{
	if (value < 10)
		return value;
	int product = 1;
	while (value)
	{
		if (value%10 != 0)
			product *= value%10;
		value /= 10;
	}
	if (array[product] == -1)
		array[product] = solve(product);
	return array[product];
}

