#define N 1000000

int a[N+10], dp[10][N+10];

int max(int x, int y) {
    return x > y ? x : y;
}

int f(int n) {
    int ans = 1;
    while (n) {
        ans *= max(n % 10, 1);
        n /= 10;
    }
    return ans;
}

int g(int n) {
    if (n < 10)
        return n;
    if (a[n] != -1)
        return a[n];
    return a[n] = g(f(n));
}

int main(int argc, char** argv) {
    int q, l, r, i, k;

    memset(a, 0xff, sizeof a);
    for (i = 1; i <= N; i++)
        a[i] = g(i);

    memset(dp, 0, sizeof dp);
    for (i = 1; i <= N; i++)
        for (k = 1; k <= 9; k++)
            dp[k][i] = dp[k][i-1] + (k==a[i]);

    scanf("%d", &q);
    while (q--) {
        scanf("%d%d%d", &l, &r, &k);
        printf("%d\n", dp[k][r] - dp[k][l-1]);
    }

    return 0;
}

