int main()
{
    int n,a,b;
    scanf("%d%d%d",&n,&a,&b);
    int temp=n,round=0,ans=1;
    while(temp!=1)
    {
        temp/=2;
        round++;
    }
    if(a==b)
        printf("1");
    while(a!=b)
    {
        if(a%2==0)
            a=a/2;
        else if(a%2!=0)
            a=(a/2)+1;
        if(b%2==0)
            b=b/2;
        else if(b%2!=0)
            b=(b/2)+1;
        if(a==b)
            break;
        else
            ans++;
    }
    if(ans == round)
        printf("Final!");
    else
        printf("%d",ans);
    return 0;
}

#include <stdio.h>
#define MAX 257

int main()
{
	int n, a, b, p[10][MAX], ans = 1, i, k, m;
	scanf("%d %d %d", &n, &a, &b);
	for(i=1;i<=n;++i)
	{
		p[ans][i] = i;
	}
	i = 1;
	while(((n >> i) & 1) == 0)
	{
		++i;
	}
	m = i;
	//printf("%d\n", i);
	k = n + 1;
	while(ans <= m)
	{
		n = k - 1;
		/*
		printf("\n%d\n", n);
		for(i=1;i<=n;++i)
		{
			printf("%d ", p[ans][i]);
		}
		*/
		k = 1;
		for(i=1;i<=n;i+=2)
		{
			//printf("%d %d\n", p[ans][i], p[ans][i + 1]);
			if((p[ans][i] == a && p[ans][i + 1] == b) || (p[ans][i] == b && p[ans][i + 1] == a))
			{
				break;
			}
			else
			{
				if(p[ans][i] == a || p[ans][i] == b)
				{
					p[ans + 1][k++] = p[ans][i];
				}
				else if(p[ans][i + 1] == a || p[ans][i + 1] == b)
				{
					p[ans + 1][k++] = p[ans][i + 1];
				}
				else
				{
					p[ans + 1][k++] = p[ans][i];
				}
			}
		}
		//printf("i = %d\n", i);
		if(i <= n)
		{
			break;
		}
		else
		{
			++ans;
		}
	}
	if(ans == m)
		printf("Final!\n");
	else
		printf("%d\n", ans);
	return 0;
}

