#include <stdio.h>
#include <stdlib.h>
#include <math.h>

int determinaRonda(int n, int a, int b, int rondaMax);

int main(int argc, char** argv) {
    int n, a, b, rondaMax, ronda;
    scanf("%d %d %d", &n, &a, &b); //Obtenemos valores de entrada
    rondaMax = round(log(n)/log(2));  //Obtenemos las ronda máxima
    ronda = determinaRonda(n, a, b, rondaMax);  //Obtenemos la ronda en donde se han quedado

    if (ronda == rondaMax){
        printf("Final!\n");
    }
    else{
        printf("%d\n", ronda);
    }    
    return (EXIT_SUCCESS);

}

int determinaRonda(int n, int a, int b, int rondaMax){
    int i, cont = 1;
    a--; b--;
    for (i = 1; i <= rondaMax; i++){
        a /= 2;
        b /= 2;
        if (a == b){  //Si son iguales es por que están jugando el mismo partido
            return cont;
        }
        else{
            cont++;
        }
    }
    return rondaMax;
}

#include <stdio.h>
