#define N 260
int E[N];
int nJugador;

int analizaJuegos(int n, int a, int b){

	int seEncuentran=0;
	int ronda=0;

	nJugador=n;
	while(!seEncuentran){
		ronda+=1;  //numero de ronda

		int nPareja=nJugador/2;
		int posi=0;
		for (int k=1; k<=nPareja;k++){
			int play1=buscajugador(n, posi);
			int play2=buscajugador(n, play1);

			if ((play1==a && play2==b)||(play1==b && play2==a)){
				seEncuentran=1;
				return ronda;
			}
			else if ((play1==a)||(play1==b)){
				E[play2]=0; //gana el favorito
			}
			else if((play2==a)||(play2==b)){
				E[play1]=0; //gana el favorito
			}
			else {
				E[play2]=0; //gana el del identificador menor
			}

			posi=play2;
		}

		nJugador=nJugador/2; //actualiza num de jugadores
	}
	return 1;
}

int buscajugador(int n, int posi){
	for(int i=posi+1; i<=n; i++){
		if(E[i]==1) return i;
	}
}

