#include <stdio.h>
#include <math.h>
#define MAX 257

int equipos[MAX];
int main(){
	int n, a, b;
	scanf("%d %d %d", &n, &a, &b);

	//marcamos los equipos favoritos de Nelson
	equipos[a] = 1;
	equipos[b] = 1;
	//utilizaremos el arreglo desde la pos 1 hasta "n"
	int final = round(log(n)/log(2));
    // printf("Final: %d\n", final);
	int ronda = 1;
	int i, k;
	int aux[MAX];
	int num = n;
	int encuentran = 0;
	while(num > 1){
		//verificamos los pares de equipos
		for(i = 1; i < num; i = i+2){
			if(equipos[i] == 1 && equipos[i+1] == 1){
				//si son los equipos favoritos terminamos
				encuentran = 1;
				break;
			}	
		}
		if(encuentran == 0){
			//si es que no se encuentran en la ronda actual
			k = 1;
			for(i=1; i<num; i=i+2){
				if(equipos[i] != equipos[i+1])
					aux[k] = 1;
				else
					aux[k] = 0;
				k++;


			}
			//en la ronda que sigue quedaran la mitad de los equipos
			num = num/2;
			k=1;
			for(i=1; i<=num; i++){
				equipos[i]=aux[k];
				k++;
			}
		}
		else{
			//si se encuentran hemos terminado
			break;
		}
		ronda++;

	}



	if(ronda == final) 
		//si se encuentran en la final
		printf("Final!\n");
	else{
		printf("%d\n", ronda);
	}
	return 0;
}

