#include <stdio.h>
#include <math.h>

int main()
{
    int n, a, b, fin,frst, mid, lst, round;

    scanf("%d %d %d",&n, &a, &b);

    if(a > b){
        a = a + b;
        b = a - b;
        a = a - b;
    }

    round = fin = log2(n);

    for(frst = 1, lst = n;;round--){
        mid = (frst+lst-1)/2;

        if(a <= mid && b > mid){
            break;
        }
        else if(b <= mid){
            lst = mid;
        }
        else{
            frst = mid + 1;
        }
    }

    if(round == fin) printf("Final!\n");
    else printf("%d\n", round);

    return 0;
}

