int ronda(float a, float b)
{

    // primero determinamos en que orden de partido jugaran
    int pa, pb;

    pa = ceil(a/2);
    pb = ceil(b/2);

    //printf("%d%d\n", pa, pb);

    // si sus rondas coinciden entonces termina la recursion
    if (pa == pb)
    {
        return 1;
    }else{
        return (1 + ronda( pa, pb)); // llamamos a la funcion con las posiciones en que quedaron suponiendo lo mejor

    }
}

