{
    int n, a, b, aux, lo, hi, r;

    scanf("%d %d %d", &n, &a, &b);

    if (a > b) {
        aux = b;
        b = a;
        a = aux;
    }

    lo = 1;
    hi = n;
    r = 0;
    while (lo < hi) {
        aux = lo + ((hi - lo) >> 1);
        if (a <= aux && b > aux) {
            if (r == 0) {
                puts("Final!");
                return 0;
            } else {
                printf("%d\n", (int) log2(n) - r);
                return 0;
            }
        } else if (a <= aux && b <= aux) {
            hi = aux;
        } else {
            lo = aux;
        }

        r++;
    }

    return 0;
}

