int max(int x, int y)
{
	if (x>y)
		return x;
	else
		return y;
}

int min(int x, int y)
{
	if (x<y)
		return x;
	else
		return y;
}

int sum(int a, int b, int c)
{
	int i,sum1=0,sum2=0;
	for (i=1;i<=(c-min(a,b));i++)
		sum1+=i;
	for (i=1;i<=(max(a,b)-c);i++)
		sum2+=i;
	return sum1+sum2;
}

