{
    int a, b;
    scanf("%d\n%d", &a, &b);
    int k1, k2, sum = 0;
    if (a >= b)
    {
        k1 = (a - b) / 2;
        k2 = a - b - k1;
    }
    else
    {
        k1 = (b - a) / 2;
        k2 = b - a - k1;
    }
    for (int i = 1; i <= k1; i++) sum += i;
    for (int i = 1; i <= k2; i++) sum += i;
    printf("%d\n", sum);
