typedef unsigned int uint;

#define MIN(a, b) ((a < b) ? a : b)
#define MAX(a, b) ((a < b) ? b : a)

int main(void)
{
	int x, y;
	fscanf(stdin, "%d%*c%d%*c", &x, &y);

	int64_t min = 1000000000;
	int tx = MIN(x, y);
	int ty = MAX(x, y);
	x = tx;
	y = ty;

	int move_x = 0;
	while((x + move_x) <= y)
	{
		int move_y = 0;
		while((y - move_y) >= x)
		{
			if((x + move_x) == (y - move_y))
			{
				int64_t t1 = (move_x * (move_x + 1)) / 2;
				int64_t t2 = (move_y * (move_y + 1)) / 2;
				if((t1 + t2) < min)
				{
					min = t1 + t2;
				}
			}
			move_y++;
		}
		move_x++;
	}
	printf("%lld\n", min);

	return 0;
}

