	int i,j,r,l,a,k;
	scanf("%d %d",&l,&r);
	a = 0;
	
	for(i = 0; i<=ceil(log(r)/log(2.0)); i++){
		for(j = 0; j<= ceil(log(r)/log(3.0)); j++){
			k = pow(2.0,i) * pow(3.0, j);
			if(l<=k && k <= r){
				a++;
			}
		}
	}
	
	printf("%d",a);
	
	return 0;
}

#include <stdio.h>

int main() {
  long long l, r, a, b, ans = 0;
   scanf("%I64d%I64d",&l, &r);
  a = 1;
  while(a <= r)
  {
      if (l <= a)
        ++ans;
      b = a * 3;
      while(b <= r)
      {
          if (b >= l)
          {
            ++ans;
          }

            b *= 3;
      }
      a *= 2;
  }
  printf("%I64d", ans);
  return 0;
}

#include <stdio.h>
#include <stdlib.h>
#include <string.h>

long long binpow2(int n)
{
    long long res = 1;
    long long a = 2;
    while (n)
    {
        if (n & 1)
            res *= a;
        a *= a;
        n >>= 1;
    }
    return res;
}

long long binpow3(int n)
{
    long long res = 1;
    long long a = 3;
    while (n)
    {
        if (n & 1)
            res *= a;
        a *= a;
        n >>= 1;
    }
    return res;
}

int main(void)
{
    long long int l, r;
    scanf("%lld%lld", &l, &r);

    int ans = 0;
    int i = 0, j = 0;
    for (; i < 40; i++)
    {
        for (j = 0; j < 40; j++)
        {
            //printf("%lld %d\n", binpow3(j), j);
            if (binpow2(i)*binpow3(j) >= 3LL*1000*1000*1000)
                break;

            long long t = binpow2(i)*binpow3(j);
            //printf("%lld\n", t);
            if (l <= t && t <= r)
                ans++;
        }
    }

    printf("%d", ans);
    return 0;
}

