#include <stdio.h>

int main(void) {
	int l, r, ans = 0;
	scanf("%d %d", &l, &r);
	long long curr_pow_2 = 1, curr_pow_3 = 1;
	while (curr_pow_2 <= r) {
		curr_pow_3 = 1;
		while (curr_pow_2 * curr_pow_3 <= r) {
			if (curr_pow_2 * curr_pow_3 >= l)
				++ans;
			curr_pow_3 *= 3;
		}
		curr_pow_2 *= 2;
	}
	printf("%d\n", ans);
	return 0;
}
